"use strict";

module.exports = class {
	constructor(){
		this.clientSet = new Set();
	}

	add(client){
		this.clientSet.add(client);
	}

	remove(client){
		this.clientSet.delete(client);
	}

	has(client){
		this.clientSet.has(client);
	}

	get size(){
		return this.clientSet.size;
	}
};