"use strict"

exports.CreateNamePacket = function(name){
	var packet = new Buffer(name.length+2);
	packet.writeUInt16BE(packet.length);
	packet.write(name, 2);
	return packet;
};

exports.CreateClientConnectPacket = function(clientId){
	var packet = new Buffer(6);
	packet.writeUInt16BE(packet.length);
	packet.writeUInt16BE(1, 2);
	packet.writeUInt16BE(clientId, 4);
	return packet;
};

exports.CreateClientClosePacket = function(clientId){
	var packet = new Buffer(6);
	packet.writeUInt16BE(packet.length);
	packet.writeUInt16BE(2, 2);
	packet.writeUInt16BE(clientId, 4);
	return packet;
};