"use strict";

module.exports = class {
	constructor(){
		this.dict = {};
		this.nextId = 0;
	}

	add(socket){
		socket.uid = ++this.nextId;
		this.dict[socket.uid] = socket;
	}

	remove(socket){
		delete this.dict[socket.uid];
	}

	findById(uid){
		return this.dict[uid];
	}

	send(uid, packet){
		var socket = this.findById(uid);
		if(socket != null)
			socket.write(packet);
	}
}